mapper.select:

select 

from wholesale_detail
where wholesaleid = #{wholesaleid,jdbcType=VARCHAR}
  and proid = #{proid,jdbcType=VARCHAR}

mapper.delete:

delete from wholesale_detail
where wholesaleid = #{wholesaleid,jdbcType=VARCHAR}
  and proid = #{proid,jdbcType=VARCHAR}

mapper.insert:

insert into wholesale_detail (wholesaleid, proid, seatid, 
  saleqty, saleprice, saletax, 
  costprice, packetqty, boxqty, 
  flowid, frprice, exrate
  )
values (#{wholesaleid,jdbcType=VARCHAR}, #{proid,jdbcType=VARCHAR}, #{seatid,jdbcType=VARCHAR}, 
  #{saleqty,jdbcType=NUMERIC}, #{saleprice,jdbcType=NUMERIC}, #{saletax,jdbcType=NUMERIC}, 
  #{costprice,jdbcType=NUMERIC}, #{packetqty,jdbcType=NUMERIC}, #{boxqty,jdbcType=NUMERIC}, 
  #{flowid,jdbcType=INTEGER}, #{frprice,jdbcType=DECIMAL}, #{exrate,jdbcType=DECIMAL}
  )
